package z;import java.nio.charset.Charset;import java.nio.file.Files;import java.nio.file.Path;import java.nio.file.Paths;import java.util.*;import java.util.stream.Collectors;public class Listword{    public List<String> list;   public Listword(String paths , String name){       try {           Path path = Paths.get(paths, name);           List<String> allLinesFromFile = Files.readAllLines(path, Charset.forName("utf-8"));           System.out.println("Text in " + path.getFileName());           for (String s : allLinesFromFile) System.out.println(s);           List<String> words = new ArrayList<>();           this.list = new ArrayList<>();           for (int i = 0; i < allLinesFromFile.size(); i++) {               words = Arrays.asList(allLinesFromFile.get(i).split("(?U)[\\pP\\s\\p{Space}]+"));               for (int j = 0; j < words.size(); j++) {                   this.list.add(words.get(j).toLowerCase());               }           }           for (int i = 0; i<list.size() ; i++){               if(list.get(i).length()==0){                   list.remove(i);               }           }       }       catch (java.io.IOException e) {           System.out.println("An error occurred.");       }   }    public List<String> getList() {        return list;    }    public void setList(List<String> list) {        this.list = list;    }    public void printList(String textAboutPrint){        System.out.println(textAboutPrint);        for (int i = 0; i < list.size(); i++) {            System.out.println((i + 1) + ". " + list.get(i));        }    }    public void deleteRepetition() {        list = list.stream().distinct().collect(Collectors.toList());    }    public void sortList(){       try {           Collections.sort(list, new SalaryAndNameComparator());       }       catch (Exception e){           System.out.println("ERROR");       }       }    @Override    public String toString() {        return "" +                "list=" + list +                '}';    }}class SalaryAndNameComparator implements Comparator<String> {    public int compare(String a, String b) {        int result;            result = a.compareToIgnoreCase(b);        return result;    }}